import React, { useState } from 'react';
import './ChemistryWorkspace.css';

const ChemistryWorkspace = ({ items, onSelectItem, apparatusList, onAddToWorkspace }) => {
  const [selectedApparatus, setSelectedApparatus] = useState(null);
  const [dropdownVisible, setDropdownVisible] = useState(false);

  const handleRightClick = (event, apparatus) => {
    event.preventDefault(); // Prevent default right-click menu
    setSelectedApparatus(apparatus);
    setDropdownVisible(true);
  };

  const handleAddToWorkspace = () => {
    if (selectedApparatus) {
      onAddToWorkspace(selectedApparatus);
      setDropdownVisible(false); // Hide dropdown after adding
    }
  };

  return (
    <div className="chem-workspace">
      <div className="workspace-header">Chemistry Lab Workspace</div>

      {/* Apparatus List */}
      <div className="lab-items">
        {apparatusList.map((apparatus) => (
          <div 
            key={apparatus.id} 
            className="lab-item" 
            onContextMenu={(e) => handleRightClick(e, apparatus)}
          >
            {apparatus.name}
          </div>
        ))}
      </div>

      {/* Dropdown Menu */}
      {dropdownVisible && (
        <div className="dropdown-menu">
          <div className="dropdown-option" onClick={handleAddToWorkspace}>
            Add to Workspace
          </div>
        </div>
      )}

      {/* Workspace Items */}
      <div className="lab-bench">
        {items.map((item) => (
          <div key={item.id} className="workspace-item" onClick={() => onSelectItem(item)}>
            {item.name}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ChemistryWorkspace;
