import React, { useState } from 'react';
import './ChemistryWorkspace.css';

const ChemistryWorkspace = ({ workspaceItems, apparatusList, onAddToWorkspace, onSelectItem }) => {
  const [dropdownVisible, setDropdownVisible] = useState(null);

  // Toggle dropdown visibility for the clicked apparatus.
  const toggleDropdown = (id) => {
    setDropdownVisible(dropdownVisible === id ? null : id);
  };

  return (
    <div className="chem-workspace">
      <div className="workspace-header">
        <h2>Chemistry Lab Workspace</h2>
        <p>Drag and drop your apparatus here or click to select.</p>
      </div>
      <div className="lab-bench">
        {/* Apparatus selection list */}
        <div className="apparatus-list">
          {apparatusList.map((apparatus) => (
            <div key={apparatus.id} className="apparatus-item">
              <button onClick={() => toggleDropdown(apparatus.id)} className="apparatus-btn">
                {apparatus.name} â¬‡
              </button>
              {dropdownVisible === apparatus.id && (
                <div className="dropdown-menu">
                  <button onClick={() => { onAddToWorkspace(apparatus); setDropdownVisible(null); }}>
                    Add to Workspace
                  </button>
                </div>
              )}
            </div>
          ))}
        </div>

        {/* Workspace items display */}
        <div className="workspace-items">
          <h3>Workspace Items</h3>
          <div className="lab-items">
            {workspaceItems.map(item => (
              <div 
                key={item.id} 
                className="lab-item" 
                onClick={() => onSelectItem(item)}
              >
                {item.name}
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChemistryWorkspace;
