import React, { useState, useEffect } from 'react';
import './App.css';
import TopBar from './components/TopBar';
import ChemistryWorkspace from './components/ChemistryWorkspace';
import ReactionDisplay from './components/ReactionDisplay';
import StepControls from './components/StepControls';
import experimentData from './data/myExperiment.json';

const apparatusImages = {
  Beaker: "https://m.media-amazon.com/images/I/61bEbTCHV5L._AC_UF1000,1000_QL80_.jpg",
  Flask: "https://www.sigmaaldrich.com/deepweb/assets/sigmaaldrich/product/images/207/642/d8e1bdca-bbcc-4545-a7ca-d30498020451/640/d8e1bdca-bbcc-4545-a7ca-d30498020451.jpg"
};

function App() {
  const [experiment, setExperiment] = useState(null);
  const [workspaceItems, setWorkspaceItems] = useState([]);
  const [currentStepIndex, setCurrentStepIndex] = useState(0);
  const [history, setHistory] = useState([]);

  useEffect(() => {
    // Load the experiment from local JSON (imported)
    setExperiment(experimentData);
    setCurrentStepIndex(0);
    setWorkspaceItems([]);
    setHistory([]);
  }, []);

  // Each step modifies the workspace
  const applyStep = (step) => {
    switch (step.action) {
      case "bringApparatus": {
        // e.g., { action: "bringApparatus", name: "Beaker", countLabel: 1 }
        const labelCount = step.countLabel || 1;
        const apparatusLabel = `${step.name}-${labelCount}`;
        const imageSrc = apparatusImages[step.name] || "https://via.placeholder.com/50";
        const newItem = {
          id: Date.now(),
          name: apparatusLabel,
          imageSrc,
          type: "apparatus",
          // Instead of single contents, we now store an array of chemicals
          chemicals: [], 
          properties: {
            position: { x: 50, y: 50 },
            color: step.color || "#aaa" // Could color the apparatus itself if desired
          }
        };
        setWorkspaceItems(prev => [...prev, newItem]);
        break;
      }
      case "addChemical": {
        // e.g., { action: "addChemical", apparatusName: "Beaker-1", chemical: "HCl", amount: 50, unit: "ml" }
        setWorkspaceItems(prev =>
          prev.map(item => {
            if (item.name === step.apparatusName) {
              // Push a new chemical object into the array
              const newChem = {
                name: step.chemical,
                amount: step.amount,
                unit: step.unit,
                color: step.color || "#999"
              };
              return {
                ...item,
                chemicals: [...item.chemicals, newChem]
              };
            }
            return item;
          })
        );
        break;
      }
      case "mix": {
        // e.g., { action: "mix", apparatusName: "Beaker-1" }
        // You could add logic to combine chemicals or rename them, etc.
        console.log(`Mixing contents in ${step.apparatusName}`);
        break;
      }
      default:
        console.warn("Unknown action:", step.action);
    }
  };

  // Revert to a previous workspace state from history
  const unapplyStep = (index) => {
    const prevState = history[index];
    if (prevState) {
      setWorkspaceItems(prevState);
    }
  };

  // Move forward a step
  const handleNext = () => {
    if (!experiment || !experiment.steps) return;
    if (currentStepIndex >= experiment.steps.length) return;
    setHistory(prev => [...prev, workspaceItems]);
    const step = experiment.steps[currentStepIndex];
    applyStep(step);
    setCurrentStepIndex(prev => prev + 1);
  };

  // Move backward a step
  const handlePrevious = () => {
    if (currentStepIndex <= 0) return;
    const prevIndex = currentStepIndex - 1;
    unapplyStep(prevIndex);
    setCurrentStepIndex(prevIndex);
    setHistory(prev => prev.slice(0, -1));
  };

  // For the TopBar, show the last completed step or default
  const safeStep = experiment?.steps?.[currentStepIndex - 1] || { number: 0, name: "N/A" };

  return (
    <div className="App">
      <TopBar
        experimentName={experiment ? experiment.experimentName : "No Experiment Loaded"}
        currentStep={safeStep}
      />
      {experiment && (
        <StepControls
          currentStepIndex={currentStepIndex}
          totalSteps={experiment.steps.length}
          onPrev={handlePrevious}
          onNext={handleNext}
        />
      )}
      <div className="main-container">
        <ReactionDisplay workspaceItems={workspaceItems} />
        <ChemistryWorkspace
          items={workspaceItems}
          onSelectItem={() => {}}
          removeItem={() => {}}
        />
      </div>
    </div>
  );
}

export default App;
