import React, { useState } from 'react';
import './App.css';
import TopBar from './components/TopBar';
import SideBar from './components/SideBar';
import SimulationControls from './components/SimulationControls';
import PropertiesPanel from './components/PropertiesPanel';
import ChemistryWorkspace from './components/ChemistryWorkspace';

const App = () => {
  // State to keep track of apparatus added to the workspace
  const [workspaceApparatus, setWorkspaceApparatus] = useState([]);

  // Function to add apparatus to the workspace
  const addApparatusToWorkspace = (apparatus) => {
    setWorkspaceApparatus([...workspaceApparatus, apparatus]);
  };

  return (
    <div className="app-container">
      <TopBar />
      <div className="main-content">
        <SideBar addApparatusToWorkspace={addApparatusToWorkspace} />
        <ChemistryWorkspace apparatusList={workspaceApparatus} />
        <PropertiesPanel />
      </div>
      <SimulationControls />
    </div>
  );
};

export default App;
