import React, { useState } from 'react';
import './SideBar.css';

const SideBar = ({ addApparatusToWorkspace }) => {
  // Local state to show/hide dropdown contents for apparatus and chemicals
  const [showApparatus, setShowApparatus] = useState(false);
  const [showChemicals, setShowChemicals] = useState(false);

  // Static list for apparatus
  const apparatusList = [
    { name: "Beaker", imageSrc: "https://example.com/beaker.jpg" },
    { name: "Flask", imageSrc: "https://example.com/flask.jpg" },
    { name: "Bunsen Burner", imageSrc: "https://example.com/bunsen_burner.jpg" }
  ];

  return (
    <div className="side-bar">
      <h3>Materials</h3>

      {/* Apparatus Dropdown */}
      <div className="dropdown">
        <button
          className="dropdown-btn"
          onClick={() => setShowApparatus(!showApparatus)}
        >
          Apparatus {showApparatus ? '▲' : '▼'}
        </button>
        {showApparatus && (
          <div className="dropdown-content">
            {apparatusList.map((item, index) => (
              <div key={index} className="material-item">
                <img src={item.imageSrc} alt={item.name} width="50" height="50" />
                <span>{item.name}</span>
                <button
                  onClick={() => addApparatusToWorkspace(item)}
                >
                  Add to Workspace
                </button>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Chemicals Dropdown */}
      <div className="dropdown">
        <button
          className="dropdown-btn"
          onClick={() => setShowChemicals(!showChemicals)}
        >
          Chemicals {showChemicals ? '▲' : '▼'}
        </button>
        {showChemicals && (
          <div className="dropdown-content">
            {/* Render chemical items here */}
          </div>
        )}
      </div>
    </div>
  );
};

export default SideBar;
