import React, { useState } from 'react';
import './App.css';
import TopBar from './components/TopBar';
import SideBar from './components/SideBar';
import SimulationControls from './components/SimulationControls';
import ChemistryWorkspace from './components/ChemistryWorkspace';
import ChemicalModal from './components/ChemicalModal';

function App() {
  const [experimentName] = useState("My Chemistry Experiment");
  const [currentStep] = useState({ number: 1, name: "Setup Lab" });
  const [simulationStatus, setSimulationStatus] = useState("stopped");
  const [workspaceItems, setWorkspaceItems] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);
  const [chemicalModalOpen, setChemicalModalOpen] = useState(false);

  // Apparatus list in sidebar (images provided)
  const apparatusList = [
    { id: 1, name: "Beaker", imageSrc: "https://m.media-amazon.com/images/I/61bEbTCHV5L._AC_UF1000,1000_QL80_.jpg" },
    { id: 2, name: "Flask", imageSrc: "https://www.sigmaaldrich.com/deepweb/assets/sigmaaldrich/product/images/207/642/d8e1bdca-bbcc-4545-a7ca-d30498020451/640/d8e1bdca-bbcc-4545-a7ca-d30498020451.jpg" },
    { id: 3, name: "Bunsen Burner", imageSrc: "https://helloartsy.com/wp-content/uploads/kids/school/how-to-draw-a-bunsen-burner/how-to-draw-a-bunsen-burner-step-4.jpg" }
  ];

  // Add an apparatus to workspace
  const addItemToWorkspace = (item) => {
    const newItem = {
      id: Date.now(),
      name: item.name,
      imageSrc: item.imageSrc,
      properties: {
        position: { x: 50, y: 50 },
        // Chemical contents initially empty:
        contents: { chemical: "", amount: 0, color: "" }
      }
    };
    setWorkspaceItems([...workspaceItems, newItem]);
  };

  // Remove apparatus from workspace by id
  const removeItemFromWorkspace = (id) => {
    setWorkspaceItems(workspaceItems.filter(item => item.id !== id));
  };

  // Update an apparatus's chemical details
  const updateApparatusChemical = (apparatusId, chemical, amount, color) => {
    setWorkspaceItems(workspaceItems.map(item => {
      if (item.id === apparatusId) {
        return {
          ...item,
          properties: {
            ...item.properties,
            contents: { chemical, amount, color }
          }
        };
      }
      return item;
    }));
  };

  const selectItem = (item) => {
    setSelectedItem(item);
  };

  const handlePlay = () => setSimulationStatus("playing");
  const handlePause = () => setSimulationStatus("paused");
  const handleStop = () => setSimulationStatus("stopped");

  return (
    <div className="App">
      <TopBar experimentName={experimentName} currentStep={currentStep} />
      <div className="main-container">
        <SideBar addItem={addItemToWorkspace} apparatusList={apparatusList} />
        <ChemistryWorkspace
          items={workspaceItems}
          onSelectItem={selectItem}
          removeItem={removeItemFromWorkspace}
        />
      </div>
      <SimulationControls
        simulationStatus={simulationStatus}
        onPlay={handlePlay}
        onPause={handlePause}
        onStop={handleStop}
      />
      {/* Chemical addition button */}
      <button className="open-chemical-modal" onClick={() => setChemicalModalOpen(true)}>
        Add Chemical
      </button>
      {chemicalModalOpen && (
        <ChemicalModal
          workspaceItems={workspaceItems}
          onClose={() => setChemicalModalOpen(false)}
          onAddChemical={updateApparatusChemical}
        />
      )}
    </div>
  );
}

export default App;
