import React from 'react';
import './ChemistryWorkspace.css';

const ChemistryWorkspace = ({ items, onSelectItem }) => {
  const workspaceStyle = {
    flex: 1,
    background: `url(${process.env.PUBLIC_URL + '/images/lab-background.jpg'}) no-repeat center center fixed`,
    backgroundSize: 'cover',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    padding: '20px',
    position: 'relative'
  };

  return (
    <div className="chem-workspace" style={workspaceStyle}>
      <div className="lab-bench">
        <h2>Chemistry Lab Workspace</h2>
        <p>Drag and drop your apparatus here or click to select.</p>
        <div className="lab-items">
          {items && items.map(item => (
            <div 
              key={item.id} 
              className="lab-item" 
              onClick={() => onSelectItem(item)}
            >
              {item.name}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ChemistryWorkspace;
