import React, { useState } from 'react';
import './App.css';
import TopBar from './components/TopBar';
import SideBar from './components/SideBar';
import SimulationControls from './components/SimulationControls';
import ChemistryWorkspace from './components/ChemistryWorkspace';
import ReactionDisplay from './components/ReactionDisplay';

function App() {
  const [experimentName] = useState("My Chemistry Experiment");
  const [currentStep] = useState({ number: 1, name: "Setup Lab" });
  const [simulationStatus, setSimulationStatus] = useState("stopped");
  const [workspaceItems, setWorkspaceItems] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);

  // Apparatus list (for the sidebar)
  const apparatusList = [
    { id: 1, name: "Beaker", imageSrc: "https://m.media-amazon.com/images/I/61bEbTCHV5L._AC_UF1000,1000_QL80_.jpg", type: "apparatus" },
    { id: 2, name: "Flask", imageSrc: "https://www.sigmaaldrich.com/deepweb/assets/sigmaaldrich/product/images/207/642/d8e1bdca-bbcc-4545-a7ca-d30498020451/640/d8e1bdca-bbcc-4545-a7ca-d30498020451.jpg", type: "apparatus" },
    { id: 3, name: "Bunsen Burner", imageSrc: "https://helloartsy.com/wp-content/uploads/kids/school/how-to-draw-a-bunsen-burner/how-to-draw-a-bunsen-burner-step-4.jpg", type: "apparatus" }
  ];

  // Chemicals list (predefined color, no direct add; added via modal or similar later)
  const chemicalsList = [
    { name: "Water", imageSrc: "https://media.istockphoto.com/id/2035384413/vector/h2o-icon-water-icon-vector.jpg?s=612x612&w=0&k=20&c=zlc43oTgVhZ6WRohUsrj_UTPWX1ox1oWEE03XTZ08QY=", type: "chemical", color: "#0000FF" },
    { name: "Ethanol", imageSrc: "https://assets.thermofisher.com/TFS-Assets/CCG/product-images/tf-80013631-ethanol-header-image.jpg-250.jpg", type: "chemical", color: "#FFA500" },
    { name: "Acetone", imageSrc: "https://study.com/cimages/multimages/16/webp.net-resizeimage24744141588368927425.png", type: "chemical", color: "#800080" }
  ];

  // Add apparatus to workspace. (Chemicals are added via a modal—see your earlier ChemicalModal.)
  const addItemToWorkspace = (item) => {
    if (item.type === "apparatus") {
      // Number apparatus uniquely, e.g., Beaker-1, Beaker-2, ...
      const count = workspaceItems.filter(i => i.type === "apparatus" && i.name.startsWith(item.name + "-")).length + 1;
      const newName = `${item.name}-${count}`;
      const newItem = {
        id: Date.now(),
        name: newName,
        imageSrc: item.imageSrc,
        type: item.type,
        properties: {
          position: { x: 50, y: 50 },
          contents: { chemical: "", amount: 0, unit:"", color: "" }
        }
      };
      setWorkspaceItems([...workspaceItems, newItem]);
    }
  };

  const removeItemFromWorkspace = (id) => {
    setWorkspaceItems(workspaceItems.filter(item => item.id !== id));
  };

  const selectItem = (item) => {
    setSelectedItem(item);
  };

  const handlePlay = () => setSimulationStatus("playing");
  const handlePause = () => setSimulationStatus("paused");
  const handleStop = () => setSimulationStatus("stopped");

  return (
    <div className="App">
      <TopBar experimentName={experimentName} currentStep={currentStep} />
      <div className="main-container">
        <SideBar 
          addItem={addItemToWorkspace} 
          apparatusList={apparatusList} 
          chemicalsList={chemicalsList}
        />
        <div className="workspace-container">
          <ReactionDisplay workspaceItems={workspaceItems} />
          <ChemistryWorkspace 
            items={workspaceItems} 
            onSelectItem={selectItem}
            removeItem={removeItemFromWorkspace}
          />
        </div>
      </div>
      <SimulationControls 
         simulationStatus={simulationStatus}
         onPlay={handlePlay}
         onPause={handlePause}
         onStop={handleStop}
      />
    </div>
  );
}

export default App;
