import { useEffect, useState } from "react";

const ChemistryWorkspace = ({ experiment }) => {
    const DEFAULT_APPARATUS = [
        { id: 1, name: "Beaker", count: 1, color: "#888" }
    ];

    const DEFAULT_CHEMICALS = [
        { id: 1, name: "Water", amount: 50, unit: "ml", color: "#0000ff" }
    ];

    const [apparatus, setApparatus] = useState([]);
    const [chemicals, setChemicals] = useState([]);

    useEffect(() => {
        if (!experiment) return;

        // Extract existing apparatus & chemicals from experiment
        const existingApparatus = experiment.steps?.filter(step => step.action === "bringApparatus") || [];
        const existingChemicals = experiment.steps?.filter(step => step.action === "addChemical") || [];

        // Set defaults if missing
        setApparatus(existingApparatus.length > 0 ? existingApparatus : DEFAULT_APPARATUS);
        setChemicals(existingChemicals.length > 0 ? existingChemicals : DEFAULT_CHEMICALS);
    }, [experiment]);

    return (
        <div className="chemistry-workspace">
            <h2>Chemistry Lab Workspace (Simulation Mode)</h2>

            <div className="section">
                <h3>Apparatus</h3>
                {apparatus.length > 0 ? (
                    <ul>
                        {apparatus.map((item) => (
                            <li key={item.id}>{item.name} (x{item.count})</li>
                        ))}
                    </ul>
                ) : (
                    <p>No apparatus available.</p>
                )}
            </div>

            <div className="section">
                <h3>Chemicals</h3>
                {chemicals.length > 0 ? (
                    <ul>
                        {chemicals.map((chem) => (
                            <li key={chem.id}>{chem.name} - {chem.amount}{chem.unit}</li>
                        ))}
                    </ul>
                ) : (
                    <p>No chemicals added yet.</p>
                )}
            </div>
        </div>
    );
};

export default ChemistryWorkspace;
