import React, { useState } from 'react';
import './SideBar.css';

const SideBar = () => {
  // Local state to show/hide dropdown contents for apparatus and chemicals
  const [showApparatus, setShowApparatus] = useState(false);
  const [showChemicals, setShowChemicals] = useState(false);

  // Static lists for apparatus and chemicals.
  // Update the imageSrc values with actual URLs or local paths as needed.
  const apparatusList = [
    { name: "Beaker", imageSrc: "https://m.media-amazon.com/images/I/61bEbTCHV5L._AC_UF1000,1000_QL80_.jpg" },
    { name: "Flask", imageSrc: "https://via.placeholder.com/50?text=Flask" },
    { name: "Bunsen Burner", imageSrc: "https://via.placeholder.com/50?text=Burner" }
  ];

  const chemicalsList = [
    { name: "Water", imageSrc: "https://via.placeholder.com/50?text=Water" },
    { name: "Ethanol", imageSrc: "https://via.placeholder.com/50?text=Ethanol" },
    { name: "Acetone", imageSrc: "https://via.placeholder.com/50?text=Acetone" }
  ];

  return (
    <div className="side-bar">
      <h3>Materials</h3>

      {/* Apparatus Dropdown */}
      <div className="dropdown">
        <button 
          className="dropdown-btn"
          onClick={() => setShowApparatus(!showApparatus)}
        >
          Apparatus {showApparatus ? '▲' : '▼'}
        </button>
        {showApparatus && (
          <div className="dropdown-content">
            {apparatusList.map((item, index) => (
              <div key={index} className="material-item">
                <img src={item.imageSrc} alt={item.name} width="50" height="50" />
                <span>{item.name}</span>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Chemicals Dropdown */}
      <div className="dropdown">
        <button 
          className="dropdown-btn"
          onClick={() => setShowChemicals(!showChemicals)}
        >
          Chemicals {showChemicals ? '▲' : '▼'}
        </button>
        {showChemicals && (
          <div className="dropdown-content">
            {chemicalsList.map((item, index) => (
              <div key={index} className="material-item">
                <img src={item.imageSrc} alt={item.name} width="50" height="50" />
                <span>{item.name}</span>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default SideBar;
