import React from 'react';
import './ChemistryWorkspace.css';

// 1) Define known apparatus & chemicals
const KNOWN_APPARATUS = {
  Beaker: {
    name: "Beaker",
    imageSrc: "/images/beaker.png",
    color: "#888"
  },
  Flask: {
    name: "Flask",
    imageSrc: "/images/flask.png",
    color: "#bbb"
  }
};

const KNOWN_CHEMICALS = {
  HCl: {
    name: "HCl",
    color: "#ff0000"
  },
  NaOH: {
    name: "NaOH",
    color: "#00ff00"
  },
  Water: {
    name: "Water",
    color: "#0000ff"
  }
};

const ChemistryWorkspace = ({ items, onSelectItem, removeItem }) => {
  // 2) Merge items with known apparatus/chemicals
  const mergedItems = items.map((item) => {
    // If the item name matches a known apparatus, override image/color/name
    const knownApp = KNOWN_APPARATUS[item.name];
    if (knownApp) {
      item = {
        ...item,
        name: knownApp.name,
        imageSrc: knownApp.imageSrc,
        properties: {
          ...item.properties,
          color: knownApp.color
        }
      };
    }

    // If there are chemicals, merge each one
    if (item.chemicals && Array.isArray(item.chemicals)) {
      item.chemicals = item.chemicals.map((chem) => {
        const knownChem = KNOWN_CHEMICALS[chem.name];
        return knownChem
          ? { ...chem, name: knownChem.name, color: knownChem.color }
          : chem;
      });
    }

    return item;
  });

  // 3) Render the merged items with your existing UI code (unchanged!)
  return (
    <div className="chem-workspace">
      <div className="workspace-header">
        <h2>Chemistry Lab Workspace (Simulation Mode)</h2>
        <p>Use Previous/Next to step through the experiment.</p>
      </div>
      <div className="lab-bench">
        <div className="lab-items">
          {mergedItems.map((item) => {
            const borderColor = item.properties?.color || "#aaa";
            return (
              <div
                key={item.id}
                className="lab-item"
                onClick={() => onSelectItem(item)}
                style={{ borderColor }}
              >
                <img
                  src={item.imageSrc}
                  alt={item.name}
                  width="60"
                  height="60"
                  className="apparatus-img"
                />
                <div className="item-label">{item.name}</div>
                <div className="item-details">
                  {item.chemicals && item.chemicals.length > 0 ? (
                    <ul className="chemical-list">
                      {item.chemicals.map((chem, idx) => (
                        <li key={idx}>
                          <span className="chem-icon">⚗️</span>
                          <strong>{chem.name}</strong>
                          &nbsp;({chem.amount}{chem.unit || ""})
                        </li>
                      ))}
                    </ul>
                  ) : (
                    <div className="no-chemicals">No chemicals added yet.</div>
                  )}
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default ChemistryWorkspace;
