import React, { useState } from "react";
import { useSpring, animated } from "@react-spring/web";
import { useDrag } from "@use-gesture/react";
import "./ChemistryWorkspace.css";

const ChemistryWorkspace = ({ items, onSelectItem, onItemPositionChange }) => {
  return (
    <div className="chem-workspace">
      <div className="workspace-header">
        <h2>Chemistry Lab Workspace</h2>
        <p>Drag and drop your apparatus here or click to select.</p>
      </div>

      <div className="lab-bench">
        <div className="lab-items">
          {items.map((item) => (
            <DraggableItem
              key={item.id}
              item={item}
              onSelectItem={onSelectItem}
              onItemPositionChange={onItemPositionChange}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

const DraggableItem = ({ item, onSelectItem, onItemPositionChange }) => {
  const [pos, setPos] = useState({ x: item.properties.position.x, y: item.properties.position.y });

  // Spring animation
  const [{ x, y }, api] = useSpring(() => ({
    x: pos.x,
    y: pos.y,
    config: { tension: 200, friction: 20 },
  }));

  // Drag gesture handling
  const bind = useDrag(({ offset: [dx, dy] }) => {
    setPos({ x: dx, y: dy });
    api.start({ x: dx, y: dy });
    onItemPositionChange(item.id, dx, dy);
  });

  return (
    <animated.div
      {...bind()}
      className="lab-item"
      style={{
        transform: `translate(${x.get()}px, ${y.get()}px)`,
      }}
      onClick={() => onSelectItem(item)}
    >
      {item.imageSrc ? (
        <img src={item.imageSrc} alt={item.name} width="50" height="50" />
      ) : (
        <div>{item.name}</div>
      )}
    </animated.div>
  );
};

export default ChemistryWorkspace;
