import React from 'react';
import Draggable from 'react-draggable'; // ➜ NEW
import './ChemistryWorkspace.css';

const ChemistryWorkspace = ({ items, onSelectItem, onItemPositionChange }) => {
  return (
    <div className="chem-workspace">
      <div className="workspace-header">
        <h2>Chemistry Lab Workspace</h2>
        <p>Drag and drop your apparatus here or click to select.</p>
      </div>

      <div className="lab-bench">
        <div className="lab-items">
          {items.map((item) => (
            <Draggable
              key={item.id}
              defaultPosition={{
                x: item.properties.position.x,
                y: item.properties.position.y
              }}
              // When drag stops, update position in parent state
              onStop={(e, data) => {
                onItemPositionChange(item.id, data.x, data.y);
              }}
            >
              <div 
                className="lab-item"
                onClick={() => onSelectItem(item)}
              >
                {/* If you’re using images, ensure item.imageSrc is set in App.js */}
                {item.imageSrc ? (
                  <img 
                    src={item.imageSrc} 
                    alt={item.name} 
                    width="50" 
                    height="50" 
                  />
                ) : (
                  // Fallback if no image
                  <div>{item.name}</div>
                )}
              </div>
            </Draggable>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ChemistryWorkspace;
