import React, { useEffect, useState } from "react";
import "./ChemistryWorkspace.css";

// Predefined items with images and colors
const KNOWN_APPARATUS = {
  "Beaker": {
    id: "beaker",
    name: "Beaker",
    image: "/images/beaker.png", // make sure this path is correct
    color: "#888"
  },
  "Flask": {
    id: "flask",
    name: "Flask",
    image: "/images/flask.png",
    color: "#bbb"
  }
};

const KNOWN_CHEMICALS = {
  "HCl": {
    id: "hcl",
    name: "HCl",
    color: "#ff0000"
  },
  "NaOH": {
    id: "naoh",
    name: "NaOH",
    color: "#00ff00"
  },
  "Water": {
    id: "water",
    name: "Water",
    color: "#0000ff"
  }
};

// Default experiment for debugging if none is passed
const defaultExperiment = {
  experimentId: "exp-001",
  experimentName: "Default Experiment",
  description: "This is a default experiment to show the workspace.",
  steps: [
    {
      action: "bringApparatus",
      name: "Beaker"
    },
    {
      action: "addChemical",
      chemical: "HCl",
      amount: 50,
      unit: "ml"
    },
    {
      action: "addChemical",
      chemical: "NaOH",
      amount: 50,
      unit: "ml"
    }
  ]
};

const ChemistryWorkspace = ({ experiment = defaultExperiment }) => {
  const [parsedSteps, setParsedSteps] = useState([]);

  useEffect(() => {
    if (!experiment || !experiment.steps) {
      setParsedSteps([]);
      return;
    }

    // Merge experiment steps with known items
    const updatedSteps = experiment.steps.map((step) => {
      if (step.action === "bringApparatus") {
        const known = KNOWN_APPARATUS[step.name];
        return known
          ? { ...step, name: known.name, color: known.color, image: known.image, id: known.id }
          : step;
      } else if (step.action === "addChemical") {
        const known = KNOWN_CHEMICALS[step.chemical];
        return known
          ? { ...step, chemical: known.name, color: known.color, id: known.id }
          : step;
      }
      return step;
    });

    setParsedSteps(updatedSteps);
  }, [experiment]);

  return (
    <div className="chemistry-workspace">
      <h2>Chemistry Lab Workspace</h2>
      {parsedSteps.length === 0 ? (
        <p>No steps found in this experiment.</p>
      ) : (
        <div className="steps-container">
          {parsedSteps.map((step, idx) => (
            <div key={idx} className="step-card">
              <p className="step-action"><strong>Action:</strong> {step.action}</p>
              {step.action === "bringApparatus" && (
                <div className="apparatus-section">
                  <p className="apparatus-name"><strong>Apparatus:</strong> {step.name}</p>
                  {step.image && (
                    <img src={step.image} alt={step.name} className="apparatus-image" />
                  )}
                </div>
              )}
              {step.action === "addChemical" && (
                <div className="chemical-section">
                  <p className="chemical-name"><strong>Chemical:</strong> {step.chemical}</p>
                  {step.amount && step.unit && (
                    <p className="chemical-amount"><strong>Amount:</strong> {step.amount} {step.unit}</p>
                  )}
                </div>
              )}
              {step.color && (
                <div className="color-indicator" style={{ backgroundColor: step.color }} />
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ChemistryWorkspace;
